<?phpclass class_portal_news{	public function __construct()	{		$this->db = zkcController::getClass("db");	}	public function getNews($uid)	{		$this->news = $this->db->getData("SELECT * FROM `news` WHERE `uid` = :uid", array('uid' => array(																					'value' => $uid,																					'type' => PDO::PARAM_INT)																								));		return $this->news ? $this->news[0] : false;		}		public function getNewses()	{		$this->newses = $this->db->getData("SELECT * FROM `news`");		return $this->newses;		}}