<?phpclass public_portal_news{	public function __construct()	{		$this->output = zkcController::getClass("output");		$this->db = zkcController::getClass("db");		zkcController::loadClass("news", "portal");		zkcController::setClass("news", new class_portal_news());		$this->newsClass = zkcController::getClass("news");		}	public function index()	{		$template = new view("newses.phtml");		$template->newses = $this->newsClass->getNewses();		$this->output->showPage($template);		}	public function news()	{		$template = new view("news.phtml");		$template->news = $this->newsClass->getNews($_GET['uid']);		$this->output->showPage($template);	}}